import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import './account.scss';
import { getAvatarUrl, getBannerUrl } from '../../utils/urls'; // ← ВОССТАНАВЛИВАЕМ ИМПОРТ

export default function Account() {
    const [user, setUser] = useState(null);
    const [editMode, setEditMode] = useState(false); // ← ТОЛЬКО ЭТУ СТРОКУ ДОБАВИЛ
    const navigate = useNavigate();

    useEffect(() => {
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
            setUser(JSON.parse(storedUser));
        } else {
            navigate('/login');
        }
    }, [navigate]);

    // ПРОСТАЯ функция загрузки картинки
    const uploadImage = async (file, type) => {
        const formData = new FormData();
        formData.append('image', file);
        formData.append('type', type);  // 'avatar' или 'banner'

        try {
            const token = localStorage.getItem('access_token');
            const response = await axios.post(
                'http://127.0.0.1:8000/api/upload-image/',
                formData,
                {
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'multipart/form-data'
                    }
                }
            );

            // Обновляем пользователя
            const updatedUser = {
                ...user,
                avatar_url: response.data.avatar_url,
                banner_url: response.data.banner_url
            };

            setUser(updatedUser);
            localStorage.setItem('user', JSON.stringify(updatedUser));
            alert('Картинка загружена!');

        } catch (error) {
            alert('Ошибка загрузки: ' + error.message);
        }
    };

    if (!user) return <div>Загрузка...</div>;

    return (
        <div className="simple-account">
            {/* Баннер */}
            <div className="banner"
                style={{
                    backgroundImage: `url(${getBannerUrl(user?.banner_url)})` // ← ВОССТАНОВИЛ
                }}>
                <img
                    src={getBannerUrl(user?.banner_url)} // ← ВОССТАНОВИЛ
                    alt="Баннер"
                    className="banner-img"
                />
                
                {/* ▼▼▼ КАРАНДАШИК БАННЕРА - ТОЛЬКО В РЕЖИМЕ РЕДАКТИРОВАНИЯ ▼▼▼ */}
                {editMode && (
                    <>
                        <input
                            type="file"
                            accept="image/*"
                            onChange={(e) => {
                                if (e.target.files[0]) uploadImage(e.target.files[0], 'banner');
                            }}
                            style={{ display: 'none' }}
                            id="banner-input"
                        />
                        <label htmlFor="banner-input" className="upload-btn">
                            ✎
                        </label>
                    </>
                )}
                {/* ▲▲▲ ТОЛЬКО ЭТО ИЗМЕНИЛ ▲▲▲ */}
            </div>

            {/* Аватар и информация */}
            <div className="profile">
                <div className="avatar-container">
                    <img
                        src={getAvatarUrl(user?.avatar_url)} // ← ВОССТАНОВИЛ
                        alt="Аватар"
                        className="avatar"
                    />
                    
                    {/* ▼▼▼ КАРАНДАШИК АВАТАРА - ТОЛЬКО В РЕЖИМЕ РЕДАКТИРОВАНИЯ ▼▼▼ */}
                    {editMode && (
                        <>
                            <input
                                type="file"
                                accept="image/*"
                                onChange={(e) => {
                                    if (e.target.files[0]) uploadImage(e.target.files[0], 'avatar');
                                }}
                                style={{ display: 'none' }}
                                id="avatar-input"
                            />
                            <label htmlFor="avatar-input" className="upload-btn small">
                                ✎
                            </label>
                        </>
                    )}
                    {/* ▲▲▲ ТОЛЬКО ЭТО ИЗМЕНИЛ ▲▲▲ */}
                </div>


                <div className="info-container">
                    <div className="info">
                        <p>{user.first_name} {user.last_name}</p>
                        <p><strong>Ник:</strong> {user.nickname}</p>
                        <p><strong>Роль:</strong> {user.role}</p>
                        
                        {/* ▼▼▼ ИЗМЕНИЛ КНОПКУ - ТЕКСТ МЕНЯЕТСЯ ▼▼▼ */}
                        <button 
                            onClick={() => setEditMode(!editMode)}
                            className="edit-btn"
                        >
                            {editMode ? 'Применить изменения' : 'Редактировать профиль'}
                        </button>
                        {/* ▲▲▲ ТОЛЬКО ЭТО ИЗМЕНИЛ ▲▲▲ */}
                    </div>
                </div>
            </div>

            <button
                onClick={() => {
                    localStorage.clear();
                    navigate('/');
                }}
                className="logout-btn"
            >
                Выйти
            </button>
        </div>
    );
}
