import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import './account.scss';

export default function Account() {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        // Получаем данные пользователя из localStorage
        const storedUser = localStorage.getItem('user');
        
        if (storedUser) {
            try {
                setUser(JSON.parse(storedUser));
            } catch (error) {
                console.error('Ошибка при парсинге данных пользователя:', error);
                localStorage.removeItem('user');
                localStorage.removeItem('access_token');
                localStorage.removeItem('refresh_token');
                navigate('/login');
            }
        } else {
            // Если пользователь не авторизован, перенаправляем на вход
            navigate('/login');
        }
        
        setLoading(false);
    }, [navigate]);

    const handleLogout = () => {
        // Очищаем данные пользователя
        localStorage.removeItem('user');
        localStorage.removeItem('access_token');
        localStorage.removeItem('refresh_token');
        
        // Перенаправляем на главную страницу
        navigate('/');
        window.location.reload(); // Обновляем страницу
    };

    if (loading) {
        return (
            <div className="account-loading">
                <p>Загрузка данных...</p>
            </div>
        );
    }

    if (!user) {
        return null; // Будет перенаправление через useEffect
    }

    return (
        <div className="account-container">
            <h1>Личный кабинет</h1>
            
            <div className="account-card">
                <div className="account-header">
                    <h2>Информация о пользователе</h2>
                </div>
                
                <div className="account-info">
                    <div className="info-row">
                        <span className="label">Имя:</span>
                        <span className="value">{user.first_name || 'Не указано'}</span>
                    </div>
                    
                    <div className="info-row">
                        <span className="label">Фамилия:</span>
                        <span className="value">{user.last_name || 'Не указано'}</span>
                    </div>
                    
                    <div className="info-row">
                        <span className="label">Никнейм:</span>
                        <span className="value nickname">{user.nickname || 'Не указан'}</span>
                    </div>
                    
                    <div className="info-row">
                        <span className="label">Роль:</span>
                        <span className={`value role ${user.role}`}>
                            {user.role === 'standart' && 'Стандарт'}
                            {user.role === 'blocked' && 'Заблокирован'}
                            {user.role === 'junior-admin' && 'Младший администратор'}
                            {user.role === 'adult-admin' && 'Старший администратор'}
                            {!['standart', 'blocked', 'junior-admin', 'adult-admin'].includes(user.role) && user.role}
                        </span>
                    </div>
                    
                    <div className="info-row">
                        <span className="label">Email:</span>
                        <span className="value">{user.email || 'Не указан'}</span>
                    </div>
                    
                    {user.phone && (
                        <div className="info-row">
                            <span className="label">Телефон:</span>
                            <span className="value">{user.phone}</span>
                        </div>
                    )}
                    
                    {user.gender && user.gender !== 'none-gender' && (
                        <div className="info-row">
                            <span className="label">Пол:</span>
                            <span className="value">
                                {user.gender === 'woman' && 'Женский'}
                                {user.gender === 'man' && 'Мужской'}
                                {user.gender !== 'woman' && user.gender !== 'man' && user.gender}
                            </span>
                        </div>
                    )}
                </div>
                
                <div className="account-actions">
                    <button onClick={handleLogout} className="logout-btn">
                        Выйти из аккаунта
                    </button>
                </div>
            </div>
            
            <div className="account-links">
                <p>
                    Хотите изменить данные? <a href="/profile/edit">Редактировать профиль</a>
                </p>
            </div>
        </div>
    );
}
